import React from "react";
import classes from "./TodoItems.module.css";
import { v4 as uuidv4 } from "uuid";

const TodoItems = ({
  input,
  setInput,
  todos,
  setTodos,
  editTodo,
  setEditTodo,
}) => {
  const updateTodo = (title, id) => {
    const newTodo = todos.map((todo) =>
      todo.id === id ? { title, id } : todo
    );
    setTodos(newTodo);
    setEditTodo("");
  };

  const submitHandler = (e) => {
    e.preventDefault();

    console.log("Hi");
    // if (!editTodo) {
    //   setTodos([...todos, { id: uuidv4(), title: input }]);
    //   setInput("");
    // } else {
    //   updateTodo(input, editTodo.id);
    // }
  };

  const editHandler = ({ id }) => {
    const findTodo = todos.find((todo) => todo.id === id);
    setEditTodo(findTodo);
  };

  const deleteHandler = ({ id }) => {
    setTodos(todos.filter((todo) => todo.id !== id));
  };

  return (
    <div>
      {todos.map((todo) => (
        <div key={todo.id}>
          <p onChange={(e) => e.preventDefault()} className={classes.todoItems}>
            {todo.title}
          </p>

          <button
            className={classes.editButton}
            onClick={() => editHandler(todo)}
          >
            {editTodo ? "SAVE" : "EDIT"}
          </button>

          <button
            className={classes.deleteButton}
            onClick={() => deleteHandler(todo)}
          >
            Delete
          </button>

          <button className={classes.deleteButton} onClick={submitHandler}>
            Temp
          </button>
        </div>
      ))}
    </div>
  );
};

export default TodoItems;
