import React, { useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";

import classes from "./Login.module.css";
import logo from "../assets/logo.png";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser, faLock } from "@fortawesome/free-solid-svg-icons";

const Login = () => {
  const {
    register,
    handleSubmit,
    trigger,
    formState: { errors },
  } = useForm();
  const onSubmit = (data) => console.log(data);

  const userRef = useRef();
  const navigate = useNavigate();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [success, setSuccess] = useState(false);

  const userIcon = <FontAwesomeIcon size="lg" icon={faUser} />;
  const lockIcon = <FontAwesomeIcon size="lg" icon={faLock} />;

  // const submitHandler = async (e) => {
  //   e.preventDefault();

  //   if (email.length >= 50) {
  //   }
  //   setErrorMsg(true);
  // };

  return (
    <div className={classes.body}>
      <div className={classes.container}>
        <div className={classes.mainBox}>
          <div className={classes.signinBox}>
            <h1>sign in</h1>
            <div className={classes.formContainer}>
              <form onSubmit={handleSubmit(onSubmit)}>
                <label htmlFor="email">Email</label>
                <div className={classes.inputForm}>
                  <span className={classes.inputIcon}>{userIcon}</span>
                  <input
                    type="email"
                    placeholder="user@rapptrlabs.com"
                    id="email"
                    ref={userRef}
                    autoComplete="off"
                    className="form-control"
                    onChange={(e) => setEmail(e.target.value)}
                    {...register("message", {
                      required: "Value is required and can't be empty",
                      maxLength: {
                        value: 50,
                        message: "Not a valid email",
                      },
                    })}
                  />
                </div>
                {errors.message && (
                  <p className={classes.errMsg}>{errors.message.message}</p>
                )}

                <label htmlFor="password">Password</label>
                <div className={classes.inputForm}>
                  <span className={classes.inputIcon}>{lockIcon}</span>
                  <input
                    type="password"
                    placeholder="password"
                    id="password"
                    ref={userRef}
                    autoComplete="off"
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </div>
                <button className={classes.button}>Login</button>
              </form>
            </div>
          </div>

          <div className={classes.leftbox}>
            <h2 className={classes.title}>
              <span className={classes.firstSpan}>Rapptr</span>
              <span> </span>
              <span className={classes.secondSpan}>Labs</span>
            </h2>
            <p>From Idea to Adoption.</p>
            <img className={classes.logo} src={logo} alt="logo" />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
