import React, { useState } from "react";
import TodoForm from "../components/TodoForm";
import classes from "../components/Todo.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faSquareMinus,
  faPenToSquare,
} from "@fortawesome/free-solid-svg-icons";

const Todo = ({ todos, completeTodo, removeTodo, updateTodo }) => {
  const deleteIcon = <FontAwesomeIcon size="lg" icon={faSquareMinus} />;
  const editIcon = <FontAwesomeIcon size="lg" icon={faPenToSquare} />;

  const [edit, setEdit] = useState({
    id: null,
    value: "",
  });

  const editHandler = (value) => {
    updateTodo(edit.id, value);
    setEdit({
      id: null,
      value: "",
    });
  };

  if (edit.id) {
    return <TodoForm edit={edit} onSubmit={editHandler} />;
  }

  return todos.map((todo, index) => (
    <div
      className={todo.isComplete ? "todo-row complete" : "todo-row"}
      key={index}
    >
      <div className={classes.todoList}>
        <div
          key={todo.id}
          onClick={() => completeTodo(todo.id)}
          className={classes.todoList}
        >
          {todo.text}
        </div>
        <div className={classes.icons}>
          <div
            onClick={() => setEdit({ id: todo.id, value: todo.text })}
            className={classes.editIcon}
          >
            {editIcon}
          </div>
          <div
            onClick={() => removeTodo(todo.id)}
            className={classes.deleteIcon}
          >
            {deleteIcon}
          </div>
        </div>
      </div>
    </div>
  ));
};

export default Todo;
