import React, { useEffect } from "react";
import classes from "./TodoItems.module.css";

const TodoItems = ({
  todos,
  setTodos,
  hideTodos,
  editTodo,
  setEditTodo,
  editingText,
  setEditingText,
}) => {
  const saveHandler = (id) => {
    const updatedTodos = [...todos].map((todo) => {
      if (todo.id === id) {
        todo.title = editingText;
      }
      return todo;
    });
    setTodos(updatedTodos);
    setEditTodo(null);
    setEditingText("");
  };

  useEffect((id) => {
    let selectedItem = todos.filter((todo) => todo.id === id);
    console.log(selectedItem);
  }, []);

  const deleteHandler = ({ id }) => {
    setTodos(todos.filter((todo) => todo.id !== id));
  };

  return (
    <div>
      {todos.map(
        (todo) =>
          !hideTodos.includes(todo.id) && (
            <div key={todo.id}>
              {editTodo === todo.id ? (
                <input
                  type="text"
                  onChange={(e) => setEditingText(e.target.value)}
                  className={classes.todoItems}
                  value={editingText}
                />
              ) : (
                <div className={classes.todos}>{todo.title}</div>
              )}

              {editTodo === todo.id ? (
                <button
                  className={classes.editButton}
                  onClick={() => saveHandler(todo.id)}
                >
                  Save
                </button>
              ) : (
                <button
                  className={classes.editButton}
                  onClick={() => setEditTodo(todo.id)}
                >
                  Edit
                </button>
              )}

              <button
                className={classes.deleteButton}
                onClick={() => deleteHandler(todo)}
              >
                Delete
              </button>
            </div>
          )
      )}
    </div>
  );
};

export default TodoItems;
