import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import classes from "./TodoList.module.css";
import TodoForm from "./TodoForm";
import TodoItems from "./TodoItems";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";

const TodoList = () => {
  const [input, setInput] = useState("");
  const [todos, setTodos] = useState([
    { id: "0", title: "Go grocery shopping" },
    { id: "1", title: "Take out Mico for a walk" },
    { id: "2", title: "Study" },
    { id: "3", title: "Workout" },
  ]);

  const [editTodo, setEditTodo] = useState(null);
  const [editingText, setEditingText] = useState("");
  const [showInput, setShowInput] = useState(false);
  const [hideTodos, setHideTodos] = useState([]);

  const searchIcon = <FontAwesomeIcon size="lg" icon={faMagnifyingGlass} />;

  const navigate = useNavigate();

  const logoutHandler = () => {
    localStorage.removeItem("user_token");
    navigate("/");
  };

  const searchHandler = (e) => {
    let hiddenItems = todos.filter(
      (i) => !i.title.toLowerCase().includes(e.target.value.toLowerCase())
    );
    let hiddenItemIds = hiddenItems.map((i) => i.id);
    setHideTodos(hiddenItemIds);
  };

  return (
    <div className={classes.body}>
      <button className={classes.logoutButton} onClick={logoutHandler}>
        Logout
      </button>
      <div className={classes.container}>
        <h2 className={classes.title}>Todo List</h2>
        <div className={classes.inputForm}>
          <span className={classes.inputIcon}>{searchIcon}</span>

          <input
            type="text"
            className={classes.searchInput}
            placeholder="Search"
            onChange={searchHandler}
          />
          <button
            className={classes.newButton}
            onClick={() => setShowInput(!showInput)}
          >
            New
          </button>
        </div>

        {showInput === true ? (
          <>
            <TodoForm
              input={input}
              setInput={setInput}
              todos={todos}
              setTodos={setTodos}
            />
          </>
        ) : (
          ""
        )}

        <TodoItems
          todos={todos}
          setTodos={setTodos}
          hideTodos={hideTodos}
          editTodo={editTodo}
          setEditTodo={setEditTodo}
          editingText={editingText}
          setEditingText={setEditingText}
        />
      </div>
    </div>
  );
};

export default TodoList;
