import React, { useState, useRef } from "react";
import classes from "../components/TodoForm.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";

function TodoForm(props) {
  const [showInput, setShowInput] = useState(false);
  const [input, setInput] = useState(props.edit ? props.edit.value : "");

  const inputRef = useRef(null);

  const searchIcon = <FontAwesomeIcon size="lg" icon={faMagnifyingGlass} />;

  const handleSearch = (e) => {
    let hiddenItems = props.todos.filter(
      (i) => !i.text.toLowerCase().find(e.target.value.toLowerCase())
    );
    let hiddenItemIds = hiddenItems.map((i) => i.id);
    props.setHideTodos(hiddenItemIds);
  };

  const handleChange = (e) => {
    setInput(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    props.onSubmit({
      id: Math.floor(Math.random() * 10000),
      text: input,
    });
    setInput("");
  };

  return (
    <div className={classes.container}>
      <div className={classes.inputForm}>
        <span className={classes.inputIcon}>{searchIcon}</span>
        <input
          placeholder="Search"
          className={classes.searchInput}
          onChange={handleSearch}
        />
        <button
          onClick={() => setShowInput(!showInput)}
          className={classes.newButton}
        >
          New
        </button>
      </div>

      {showInput === true ? (
        <>
          <form onSubmit={handleSubmit} className={classes.todoForm}>
            {props.edit ? (
              <div>
                <input
                  value={input}
                  onChange={handleChange}
                  name="text"
                  ref={inputRef}
                  className={classes.todoInput}
                />
                <button onClick={handleSubmit} className={classes.addButton}>
                  Update
                </button>
              </div>
            ) : (
              <div>
                <input
                  value={input}
                  onChange={handleChange}
                  name="text"
                  className={classes.todoInput}
                  ref={inputRef}
                />
                <button onClick={handleSubmit} className={classes.addButton}>
                  Save
                </button>
              </div>
            )}
          </form>
        </>
      ) : (
        ""
      )}
    </div>
  );
}

export default TodoForm;
