import React, { useState } from "react";
import TodoForm from "../components/TodoForm";
import Todo from "./Todo";
import classes from "../components/TodoList.module.css";

function TodoList() {
  const [todos, setTodos] = useState([
    {
      id: 0,
      text: "Go grocery shopping",
    },
    {
      id: 1,
      text: "Study",
    },
    {
      id: 2,
      text: "Take Mico out for a walk",
    },
    {
      id: 3,
      text: "Workout",
    },
  ]);

  const [hideTodos, setHideTodos] = useState([]);

  const addTodo = (todo) => {
    if (!todo.text || /^\s*$/.test(todo.text)) {
      return;
    }

    const newTodos = [...todos, todo];

    setTodos(newTodos);
    console.log(...todos);
  };

  const updateTodo = (todoId, newValue) => {
    if (!newValue.text || /^\s*$/.test(newValue.text)) {
      return;
    }

    setTodos((prev) =>
      prev.map((item) => (item.id === todoId ? newValue : item))
    );
  };

  const removeTodo = (id) => {
    const removedArr = [...todos].filter((todo) => todo.id !== id);

    setTodos(removedArr);
  };

  const completeTodo = (id) => {
    let updatedTodos = todos.map((todo) => {
      if (todo.id === id) {
        todo.isComplete = !todo.isComplete;
      }
      return todo;
    });
    setTodos(updatedTodos);
  };

  return (
    <div className={classes.body}>
      <div className={classes.title}>Todo List</div>
      <TodoForm onSubmit={addTodo} todos={todos} setHideTodos={setHideTodos} />
      <Todo
        todos={todos}
        completeTodo={completeTodo}
        removeTodo={removeTodo}
        updateTodo={updateTodo}
        hideTodos={hideTodos}
      />
    </div>
  );
}

export default TodoList;
