import React, { useRef, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import classes from "./Login.module.css";
import logo from "../assets/logo.png";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser, faLock } from "@fortawesome/free-solid-svg-icons";

const Login = () => {
  const axios = require("axios").default;
  const userRef = useRef();
  const navigate = useNavigate();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMsg, setErrorMsg] = useState("");
  const [success, setSuccess] = useState(false);

  const userIcon = <FontAwesomeIcon size="lg" icon={faUser} />;
  const lockIcon = <FontAwesomeIcon size="lg" icon={faLock} />;

  useEffect(() => {
    userRef.current.focus();
  }, []);

  useEffect(() => {
    setErrorMsg("");
  }, [email, password]);

  const submitHandler = async (e) => {
    e.preventDefault();

    if (email.length === 0 || email.length >= 50) {
      setErrorMsg(true);
    }
  };

  return (
    <div className={classes.body}>
      <div className={classes.container}>
        <div className={classes.mainBox}>
          <div className={classes.signinBox}>
            <h1>sign in</h1>
            <div className={classes.formContainer}>
              <form onSubmit={submitHandler}>
                <label htmlFor="email">Email</label>
                <div className={classes.inputForm}>
                  <span className={classes.inputIcon}>{userIcon}</span>
                  <input
                    type="email"
                    placeholder="user@rapptrlabs.com"
                    id="email"
                    ref={userRef}
                    autoComplete="off"
                    onChange={(e) => setEmail(e.target.value)}
                    value={email}
                    maxLength="50"
                    required
                  />
                </div>

                <label htmlFor="password">Password</label>
                <div className={classes.inputForm}>
                  <span className={classes.inputIcon}>{lockIcon}</span>
                  <input
                    type="password"
                    placeholder="password"
                    id="password"
                    ref={userRef}
                    autoComplete="off"
                    onChange={(e) => setPassword(e.target.value)}
                    value={password}
                    required
                  />
                </div>
                <button className={classes.button}>Login</button>
              </form>
            </div>
          </div>

          <div className={classes.leftbox}>
            <h2 className={classes.title}>
              <span className={classes.firstSpan}>Rapptr</span>
              <span> </span>
              <span className={classes.secondSpan}>Labs</span>
            </h2>
            <p>From Idea to Adoption.</p>
            <img className={classes.logo} src={logo} alt="logo" />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
