import React, { useState } from "react";
import classes from "./TodoList.module.css";
import TodoForm from "./TodoForm";
import TodoItems from "./TodoItems";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";

const TodoList = (props) => {
  const [input, setInput] = useState("");
  const [todos, setTodos] = useState([
    { id: "0", title: "Go grocery shopping" },
    { id: "1", title: "Take out Mico for a walk" },
    { id: "2", title: "Study" },
    { id: "3", title: "Workout" },
  ]);
  const [editTodo, setEditTodo] = useState(null);
  const [showInput, setShowInput] = useState(false);
  const [hideTodos, setHideTodos] = useState([]);

  const searchIcon = <FontAwesomeIcon size="lg" icon={faMagnifyingGlass} />;

  const searchHandler = (e) => {
    console.log(e.target.value);
    let hiddenItems = props.todos.filter(
      (i) => !i.text.toLowerCase().includes(e.target.value.toLowerCase())
    );
    let hiddenItemIds = hiddenItems.map((i) => i.id);
    props.setHideTodos(hiddenItemIds);
    console.log(hiddenItemIds);
  };

  return (
    <div className={classes.body}>
      <div className={classes.container}>
        <h2 className={classes.title}>Todo List</h2>

        <div className={classes.inputForm}>
          <span className={classes.inputIcon}>{searchIcon}</span>
          <input
            type="text"
            className={classes.searchInput}
            placeholder="Search"
            onChange={searchHandler}
          />
          <button
            className={classes.newButton}
            onClick={() => setShowInput(!showInput)}
          >
            New
          </button>
        </div>

        {showInput === true ? (
          <>
            <TodoForm
              input={input}
              setInput={setInput}
              todos={todos}
              setTodos={setTodos}
              editTodo={editTodo}
              setEditTodo={setEditTodo}
              setHideTodos={setHideTodos}
            />
          </>
        ) : (
          ""
        )}

        <TodoItems
          todos={todos}
          setTodos={setTodos}
          setEditTodo={setEditTodo}
          hideTodos={hideTodos}
        />
      </div>
    </div>
  );
};

export default TodoList;
