import React, { useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";

import classes from "./Login.module.css";
import logo from "../assets/logo.png";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser, faLock } from "@fortawesome/free-solid-svg-icons";
import axios from "axios";

const Login = () => {
  // eslint-disable-next-line
  const [email, setEmail] = useState("");
  // eslint-disable-next-line
  const [password, setPassword] = useState("");

  const userRef = useRef();
  const navigate = useNavigate();

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  const onSubmit = (data) => {
    console.log(email);
    // axios({
    //   method: "post",
    //   url: "http://dev.rapptrlabs.com/Tests/scripts/user-login.php",
    //   data: {
    //     email: "test@rapptrlabs.com",
    //     password: "Test123",
    //   },
    //   headers: {
    //     "Access-Control-Allow-Origin": "*",
    //   },
    // })
    //   .then(function (response) {
    //     console.log(response);
    //   })
    //   .catch(function (error) {
    //     console.log(error);
    //   });
  };

  const userIcon = <FontAwesomeIcon size="lg" icon={faUser} />;
  const lockIcon = <FontAwesomeIcon size="lg" icon={faLock} />;

  return (
    <div className={classes.body}>
      <div className={classes.container}>
        <div className={classes.mainBox}>
          <div className={classes.signinBox}>
            <h1>sign in</h1>
            <div className={classes.formContainer}>
              <form onSubmit={handleSubmit(onSubmit)}>
                <label htmlFor="email">Email</label>
                <div className={classes.inputForm}>
                  <span className={classes.inputIcon}>{userIcon}</span>
                  <input
                    type="email"
                    placeholder="user@rapptrlabs.com"
                    id="email"
                    ref={userRef}
                    autoComplete="off"
                    className={errors.email && "invalid"}
                    onChange={(e) => setEmail(e.target.value)}

                    // {...register("email", {
                    //   required: "Value is required and can't be empty",
                    //   maxLength: {
                    //     value: 50,
                    //     message: "Not a valid email",
                    //   },
                    // })}
                  />
                </div>
                {errors.email && (
                  <p className={classes.errMsg}>{errors.email.message}</p>
                )}

                <label htmlFor="password">Password</label>
                <div className={classes.inputForm}>
                  <span className={classes.inputIcon}>{lockIcon}</span>
                  <input
                    type="password"
                    placeholder="Must be at least 4 characters"
                    id="password"
                    ref={userRef}
                    autoComplete="off"
                    className={errors.password && "invalid"}
                    onChange={(e) => setPassword(e.target.value)}
                    {...register("password", {
                      required: "Value is required and can't be empty",
                      minLength: {
                        value: 4,
                        message: "Password must be 4-16 characters long",
                      },
                      maxLength: {
                        value: 16,
                        message: "Password must be 4-16 characters long",
                      },
                    })}
                  />
                </div>
                {errors.password && (
                  <p className={classes.errMsg}>{errors.password.message}</p>
                )}
                <button
                  className={
                    (errors.email && "invalid-button") ||
                    (errors.password && "invalid-button")
                  }
                >
                  Login
                </button>
              </form>
            </div>
          </div>

          <div className={classes.leftbox}>
            <h2 className={classes.title}>
              <span className={classes.firstSpan}>Rapptr</span>
              <span> </span>
              <span className={classes.secondSpan}>Labs</span>
            </h2>
            <p>From Idea to Adoption.</p>
            <img className={classes.logo} src={logo} alt="logo" />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
